import { MdDeleteForever } from 'react-icons/md';
import { useState } from 'react';

function Note({id, text, date, handleEditClick, handleDeleteNote}) {

	return (
		<div className='note'>
			<span>{text}</span>
			<div className='note-footer'>
				<small>{date}</small>
				<button
					onClick={() => handleEditClick()}
					className='edit-icon'
					size='1.3em'
				>Edit</button>
				<MdDeleteForever
					onClick={() => handleDeleteNote(id)}
					className='delete-icon'
					size='1.3em'
				/>
			</div>
		</div>
	);
};

export default Note;
