       setNameState(props);
   }, [props])

	if (edit !== true) {
		return (
			<div className='note' onClick={() => updateMovingNote(id)}>
				<span>{noteText}</span>
				<div className='note-footer'>
					<small>{date}</small>
					<button
						onClick={() => setEdit(true)}
					>
					Edit
					</button>
					<MdDeleteForever
						onClick={() => handleDeleteNote(id)}
						className='delete-icon'
						size='1.3em'
					/>
				</div>
			</div>);
	} else {
		return (
			<div className='note-new'>
				<textarea
					rows='10'
					cols='10'
					placeholder='Type to Add Your Note'
					value={noteText}
					onChange={handleChange}
				></textarea>
				<div className='note-footer'>
					<small>
						{characterLimit - noteText.length} Remaining
					</small>
					<button className='save' onClick={handleSaveClick}>
						Change
					</button>
				</div>
			</div>
		)
	};
};

export default Note;
